body {
    margin: 0;
    font-family: Arial, sans-serif;
}

.navbar {
    background-color: #4db6ac;
    padding: 10px 20px;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
    width: 100%;
}

.navbar-menu {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;  /*Center align the links*/
    /*gap: 20px;*/
}

.nav-item {
    color: white;
    text-decoration: none;
    padding: 10px 15px;
    display: flex;
    border-radius: 5px;
    transition: background-color 0.3s;
}

.nav-item:hover {
    background-color: #198754; /* Adjust color as needed */
    cursor: pointer;
}

.nav-item i {
    margin-right: 8px;
}

/* Example icons using FontAwesome, make sure to include FontAwesome in your project */
/*@import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css');*/

.icon-home::before {
    content: "\f015"; /* FontAwesome home icon */
}

.icon-branch::before {
    content: "\f0c9"; /* FontAwesome bars icon for branch */
}

.icon-config::before {
    content: "\f013"; /* FontAwesome cog icon */
}

.icon-alarm::before {
    content: "\f0f3"; /* FontAwesome bell icon */
}

.icon-reports::before {
    content: "\f15c"; /* FontAwesome file icon */
}

.icon-analytics::before {
    content: "\f080"; /* FontAwesome chart-line icon */
}

/* Media Queries for Responsive Design */

/* Large devices (desktops, 992px and up) */
@media (max-width: 1200px) {
    .navbar-menu {
        gap: 15px;
    }

    .nav-item {
        padding: 8px 12px;
    }
}

/* Medium devices (tablets, 768px and up) */
@media (max-width: 992px) {
    .navbar-menu {
        gap: 10px;
    }

    .nav-item {
        padding: 6px 10px;
    }
}

/* Small devices (landscape phones, 576px and up) */
@media (max-width: 768px) {
    .navbar-menu {
        flex-direction: column;
        gap: 10px;
    }

    .nav-item {
        width: 100%;
        justify-content: center;
        padding: 8px 15px;
    }
}

/* Extra small devices (portrait phones, less than 576px) */
@media (max-width: 576px) {
    .navbar {
        padding: 5px 10px;
    }

    .nav-item {
        padding: 5px 10px;
    }
}

/* Specific breakpoints for Acer and Lenovo ThinkPad devices */
/* Common laptop resolution 1366x768 */
@media (max-width: 1366px) and (max-height: 768px) {
    .navbar-menu {
        gap: 12px;
    }

    .nav-item {
        padding: 7px 13px;
    }
}

/* Common laptop resolution 1920x1080 */
@media (max-width: 1920px) and (max-height: 1080px) {
    .navbar-menu {
        gap: 18px;
    }

    .nav-item {
        padding: 9px 16px;
    }
}
.logo {
        margin-right: 0; /* Remove margin */
    }

